  def determineFloorMinus1(str: String): Int = {
    var floor = 0
    var positionFound = -1
    str.zipWithIndex.toArray.foreach{ case (c, idx) =>
      if (c == '(') floor += 1
      else floor -= 1
      if (floor == -1 && positionFound == -1) {
        positionFound = idx+1
      }
    }
    positionFound
  }
